mapper.select:
select *
from t_order t inner join t_shop ts
on ts.SHOP_ID = t.SHOP_ID

    and ts.DISTR_ID = #{params.distrId}


    and t.DATM >= #{params.start}


    and t.DATM <= #{params.end}


    and t.deleted = #{params.deleted}

select

    t.STATE as state,

count(t.ID) as count,
sum(t.AMOUNT) as sum,
max(t.DATM) as maxTime,
min(t.DATM) as minTime
from t_order t inner join t_shop ts
on ts.SHOP_ID = t.SHOP_ID
and ts.DISTR_ID = #{params.distrId}

    and ts.SHOP_ID = #{params.shopId}


    and t.STATE = #{params.state}


    and t.STATE >= #{params.amount}


    and t.DATM >= #{params.start}


    and t.DATM <= #{params.end}


    and t.deleted = #{params.deleted}


    group by t.STATE

select

from t_shop ts
inner join t_order t on ts.SHOP_ID = t.SHOP_ID
and t.ID = #{id}

    and t.deleted = #{deleted}

select

from t_shop ts
inner join t_order t on ts.SHOP_ID = t.SHOP_ID

    and ts.DISTR_ID = #{distrId}


    and t.STATE = #{state}


    and t.deleted = #{deleted}

select

from t_shop ts
inner join t_order t on ts.SHOP_ID = t.SHOP_ID
and ts.DISTR_ID = #{distrId}

    and t.STATE in

        #{item}



    and t.deleted = #{deleted}

select t.ID as order_id, t.STATE as order_state, t.AMOUNT as order_amount, t.DATM as order_datm, t.PROFIT as order_profit, t.VERIFY_TM as order_verify_tm, t.BASE_P as order_base_p, ts.SHOP_ID as shop_id, ts.SHOP_NM as shop_nm from t_shop ts inner join t_order t on ts.SHOP_ID = t.SHOP_ID
and t.STATE= #{state}
and ts.DISTR_ID = #{distrId} and ts.SHOP_NM like concat('%', #{pg.keyword}, '%')

select t.ID as order_id, t.STATE as order_state, t.AMOUNT as order_amount, t.DATM as order_datm, t.PROFIT as order_profit, t.VERIFY_TM as order_verify_tm, t.BASE_P as order_base_p, ts.SHOP_ID as shop_id, ts.SHOP_NM as shop_nm from t_shop ts inner join t_order t on ts.SHOP_ID = t.SHOP_ID and ts.DISTR_ID = #{distrId}
and t.STATE= #{state}
inner join t_order_d tod on t.ID = tod.ORDER_ID inner join t_paf_comm tpc on tod.COMM_ID = tpc.COMM_ID and tpc.COMM_NM like concat('%', #{pg.keyword}, '%') group by t.ID

select *
from t_order
where STATE like #{pg.keyword}

SELECT *
FROM t_order
WHERE shop_id IN (
    SELECT shop_id
    FROM t_shop
    WHERE distr_id = #{distr_id}
)
  AND state = #{state}
