mapper.select:
update t_sku_stock
set locked_stock=locked_stock + #{num}
where id = #{id}

update t_sku_stock
set locked_stock=locked_stock - #{num},
    stock=stock - #{num}
where paf_comm_id = #{pcid}
  and sku_id = #{sid}

update t_sku_stock
set locked_stock=locked_stock - #{num}
where paf_comm_id = #{pcid}
  and sku_id = #{sid}
